"use client";

import { useState } from "react";
import { AppShell } from "@/components/layout/app-shell";
import {
  Button,
  ScrollArea,
  Avatar,
  AvatarFallback,
  AvatarImage,
  Input,
} from "@repo/ui";
import { Send, Bot, User, Sparkles } from "lucide-react";

interface Message {
  id: string;
  role: "user" | "assistant";
  content: string;
}

export default function ChatPage() {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: "1",
      role: "assistant",
      content:
        "Hello! I'm your AI asset assistant. I can help you find files using semantic understanding. What are you looking for today?",
    },
  ]);
  const [inputValue, setInputValue] = useState("");

  const handleSend = () => {
    if (!inputValue.trim()) return;

    const newMessage: Message = {
      id: Date.now().toString(),
      role: "user",
      content: inputValue,
    };

    setMessages((prev) => [...prev, newMessage]);
    setInputValue("");

    // Mock AI response
    setTimeout(() => {
      setMessages((prev) => [
        ...prev,
        {
          id: (Date.now() + 1).toString(),
          role: "assistant",
          content: `I understand you're looking for "${inputValue}". I'm searching across all assets including video transcripts and document contents...`,
        },
      ]);
    }, 1000);
  };

  return (
    <AppShell>
      <div className="mx-auto flex h-[calc(100vh-theme(spacing.12)-theme(spacing.6))] w-full max-w-4xl flex-col">
        <div className="flex-1 overflow-hidden p-4">
          <ScrollArea className="h-full pr-4">
            <div className="space-y-6">
              {messages.map((message) => (
                <div
                  key={message.id}
                  className={`flex gap-3 ${
                    message.role === "user" ? "flex-row-reverse" : "flex-row"
                  }`}
                >
                  <Avatar className="h-8 w-8">
                    {message.role === "assistant" ? (
                      <div className="bg-primary/10 flex h-full w-full items-center justify-center">
                        <Sparkles className="text-primary h-4 w-4" />
                      </div>
                    ) : (
                      <AvatarImage src="https://github.com/shadcn.png" />
                    )}
                    <AvatarFallback>
                      {message.role === "assistant" ? "AI" : "ME"}
                    </AvatarFallback>
                  </Avatar>
                  <div
                    className={`max-w-[80%] rounded-lg p-3 text-sm ${
                      message.role === "user"
                        ? "bg-primary text-primary-foreground"
                        : "bg-muted"
                    }`}
                  >
                    {message.content}
                  </div>
                </div>
              ))}
            </div>
          </ScrollArea>
        </div>

        <div className="bg-background/95 supports-[backdrop-filter]:bg-background/60 border-t p-4 backdrop-blur">
          <form
            onSubmit={(e) => {
              e.preventDefault();
              handleSend();
            }}
            className="flex gap-2"
          >
            <Input
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              placeholder="Ask anything about your assets..."
              className="flex-1"
            />
            <Button type="submit" size="icon" disabled={!inputValue.trim()}>
              <Send className="h-4 w-4" />
            </Button>
          </form>
        </div>
      </div>
    </AppShell>
  );
}

"use client";

import { AppShell } from "@/components/layout/app-shell";
import { Card, CardContent, CardHeader, CardTitle } from "@repo/ui";
import { Folder, FileIcon } from "lucide-react";

export default function CollectionsPage() {
  const collections = [
    {
      id: "1",
      name: "Marketing Campaigns",
      itemCount: 45,
      thumbnail:
        "https://images.unsplash.com/photo-1556761175-b413da4baf72?w=400",
    },
    {
      id: "2",
      name: "Product Photos",
      itemCount: 128,
      thumbnail:
        "https://images.unsplash.com/photo-1526947425960-945c6e72858f?w=400",
    },
    {
      id: "3",
      name: "Brand Assets",
      itemCount: 67,
      thumbnail:
        "https://images.unsplash.com/photo-1558655146-364adaf1fcc9?w=400",
    },
  ];

  return (
    <AppShell>
      <div className="p-6">
        <div className="mb-6">
          <h1 className="text-2xl font-semibold">Collections</h1>
          <p className="text-muted-foreground text-sm">
            Organize your assets into collections
          </p>
        </div>

        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
          {collections.map((collection) => (
            <Card
              key={collection.id}
              className="cursor-pointer transition-all hover:shadow-md"
            >
              <div className="bg-muted aspect-video w-full overflow-hidden rounded-t-lg">
                <img
                  src={collection.thumbnail}
                  alt={collection.name}
                  className="h-full w-full object-cover"
                />
              </div>
              <CardHeader className="p-4">
                <CardTitle className="flex items-center gap-2 text-base">
                  <Folder className="h-4 w-4" />
                  {collection.name}
                </CardTitle>
              </CardHeader>
              <CardContent className="p-4 pt-0">
                <p className="text-muted-foreground text-sm">
                  {collection.itemCount} items
                </p>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </AppShell>
  );
}

"use client";

import { AppShell } from "@/components/layout/app-shell";
import { AssetCard } from "@/components/assets/asset-card";
import { mockAssets } from "@/lib/mock-data";
import { Badge } from "@repo/ui";
import {
  Filter,
  ArrowUpDown,
  FolderIcon,
  Clock,
  Bookmark,
  Sparkles,
} from "lucide-react";
import { Button } from "@repo/ui";
import { SearchInput } from "@/components/search/search-input";
import Link from "next/link";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
  ScrollArea,
} from "@repo/ui";

export default function HomePage() {
  const recentSearches = [
    { term: "Quarterly Report", mode: "global", time: "2h ago" },
    { term: "Marketing Assets 2024", mode: "semantic", time: "5h ago" },
    { term: "Product Launch Video", mode: "global", time: "1d ago" },
  ];

  const savedSearches = [
    { name: "My Design Files", count: 12 },
    { name: "Pending Review", count: 5 },
    { name: "High Res Images", count: 24 },
  ];

  return (
    <AppShell>
      <div className="flex min-h-[calc(100vh-theme(spacing.12)-theme(spacing.6))] flex-col">
        {/* Hero Search Section */}
        <div className="flex flex-1 flex-col items-center justify-center p-8 pb-12 transition-all">
          <div className="animate-in fade-in slide-in-from-bottom-4 w-full max-w-3xl space-y-8 duration-500">
            <div className="space-y-2 text-center">
              <h1 className="text-3xl font-semibold tracking-tight">
                What are you looking for?
              </h1>
              <p className="text-muted-foreground">
                Search across {mockAssets.length} assets using keywords,
                metadata, or natural language.
              </p>
            </div>

            <SearchInput variant="hero" autoFocus />

            {/* Quick Access Grid */}
            <div className="grid grid-cols-1 gap-4 pt-4 md:grid-cols-2">
              {/* Recent Searches */}
              <Card className="bg-muted/30 border-none shadow-none">
                <CardHeader className="p-4 pb-2">
                  <CardTitle className="text-muted-foreground flex items-center gap-2 text-sm font-medium">
                    <Clock className="h-3.5 w-3.5" />
                    Recent Searches
                  </CardTitle>
                </CardHeader>
                <CardContent className="p-4 pt-2">
                  <div className="space-y-1">
                    {recentSearches.map((item, i) => (
                      <Link
                        key={i}
                        href={`/search?q=${encodeURIComponent(item.term)}&mode=${item.mode}`}
                        className="hover:bg-accent group flex items-center justify-between rounded-md p-2 text-sm transition-colors"
                      >
                        <span className="flex items-center gap-2">
                          {item.mode === "semantic" && (
                            <Sparkles className="text-primary h-3 w-3" />
                          )}
                          {item.term}
                        </span>
                        <span className="text-muted-foreground group-hover:text-foreground/70 text-xs">
                          {item.time}
                        </span>
                      </Link>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Saved Searches */}
              <Card className="bg-muted/30 border-none shadow-none">
                <CardHeader className="p-4 pb-2">
                  <CardTitle className="text-muted-foreground flex items-center gap-2 text-sm font-medium">
                    <Bookmark className="h-3.5 w-3.5" />
                    Saved Searches
                  </CardTitle>
                </CardHeader>
                <CardContent className="p-4 pt-2">
                  <div className="space-y-1">
                    {savedSearches.map((item, i) => (
                      <button
                        key={i}
                        className="hover:bg-accent group flex w-full items-center justify-between rounded-md p-2 text-sm transition-colors"
                      >
                        <span>{item.name}</span>
                        <Badge
                          variant="outline"
                          className="bg-background h-5 px-1.5 text-[10px]"
                        >
                          {item.count}
                        </Badge>
                      </button>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>

        {/* Recent Uploads (Collapsed view) */}
        <div className="bg-muted/10 space-y-4 border-t p-6">
          <div className="flex items-center justify-between">
            <h2 className="text-muted-foreground text-sm font-semibold uppercase tracking-wider">
              Recent Uploads
            </h2>
            <Button variant="ghost" size="sm" asChild>
              <Link href="/search?sort=recent" className="text-xs">
                View All
              </Link>
            </Button>
          </div>

          <div className="grid grid-cols-2 gap-4 sm:grid-cols-4 md:grid-cols-5 lg:grid-cols-6 xl:grid-cols-7">
            {mockAssets.slice(0, 7).map((asset) => (
              <AssetCard
                key={asset.id}
                asset={asset}
                className="bg-card hover:border-primary/50 aspect-[4/5] cursor-context-menu border transition-all"
              />
            ))}
          </div>
        </div>
      </div>
    </AppShell>
  );
}

"use client";

import { useEffect, useState, Suspense } from "react";
import { useSearchParams } from "next/navigation";
import { AppShell } from "@/components/layout/app-shell";
import { SearchInput } from "@/components/search/search-input";
import { ModeBadge } from "@/components/search/mode-badge";
import { FilterPanel, FilterTrigger } from "@/components/search/filter-panel";
import { AssetCard } from "@/components/assets/asset-card";
import { WhyMatched } from "@/components/search/why-matched";
import { ExploreMore } from "@/components/search/explore-more";
import {
  Button,
  Card,
  Skeleton,
  Sheet,
  SheetContent,
  SheetHeader,
  SheetTitle,
  Separator,
  ToggleGroup,
  ToggleGroupItem,
} from "@repo/ui";

import {
  Grid3X3,
  List,
  MessageSquare,
  Bookmark,
  ChevronDown,
  Sparkles,
} from "lucide-react";
import Link from "next/link";
import { useSearchStore } from "@/lib/stores/search-store";
import {
  mockFilterFacets,
  mockExploreSuggestions,
  mockAssets,
  createSearchResult,
} from "@/lib/mock-data";
import type { SearchMode } from "@/lib/types";
import { cn } from "@repo/ui";

function SearchResultsContent() {
  const searchParams = useSearchParams();
  const {
    query,
    setQuery,
    mode,
    setMode,
    results,
    isLoading,
    viewMode,
    setViewMode,
    filters,
    setFilters,
    search,
    saveSearch,
    hasSearched,
  } = useSearchStore();

  const [showFilters, setShowFilters] = useState(false);

  // Initialize from URL params
  useEffect(() => {
    const q = searchParams.get("q");
    const m = searchParams.get("mode") as SearchMode | null;

    if (q && q !== query) {
      setQuery(q);
    }
    if (m && m !== mode) {
      setMode(m);
    }
    if (q) {
      search();
    }
  }, [searchParams, query, mode, setQuery, setMode, search]);

  const handleSaveSearch = () => {
    const name = prompt("Name this search:", query.slice(0, 30));
    if (name) {
      saveSearch(name);
    }
  };

  const activeFilterCount = Object.values(filters).reduce((acc, val) => {
    if (Array.isArray(val)) return acc + val.length;
    if (val && typeof val === "object") return acc + 1;
    return acc;
  }, 0);

  // For demo purposes, if no results and hasSearched is false, show some mock results
  const displayResults =
    results.length > 0
      ? results
      : hasSearched
        ? []
        : mockAssets.slice(0, 9).map((asset) =>
            createSearchResult(
              asset,
              [
                {
                  type: "keyword",
                  matchedTerms: ["demo"],
                  explanation: "Sample result for demonstration",
                },
              ],
              0.8 + Math.random() * 0.2
            )
          );

  return (
    <AppShell>
      <div className="bg-background min-h-screen">
        {/* Helper text for context since Search is now in AppShell header */}
        <div className="bg-muted/30 border-b">
          <div className="text-muted-foreground container px-4 py-2 text-xs">
            Search Mode:{" "}
            {mode === "global"
              ? "Global"
              : mode === "semantic"
                ? "Semantic"
                : "Advanced"}
          </div>
        </div>

        {/* Main content */}
        <div className="container px-4 py-6">
          {/* Results header */}
          <div className="mb-6 flex flex-col justify-between gap-4 sm:flex-row sm:items-center">
            {/* Query summary */}
            <div className="flex flex-wrap items-center gap-3">
              {query && (
                <>
                  <h1 className="text-lg font-medium">
                    Results for &ldquo;{query}&rdquo;
                  </h1>
                  <ModeBadge mode={mode} />
                </>
              )}
              {!isLoading && (
                <span className="text-muted-foreground text-sm">
                  {displayResults.length} results
                </span>
              )}
            </div>

            {/* Actions */}
            <div className="flex flex-wrap items-center gap-2">
              {/* View mode toggle */}
              <ToggleGroup
                type="single"
                value={viewMode}
                onValueChange={(v) => v && setViewMode(v as "grid" | "list")}
              >
                <ToggleGroupItem value="grid" size="sm" aria-label="Grid view">
                  <Grid3X3 className="h-4 w-4" />
                </ToggleGroupItem>
                <ToggleGroupItem value="list" size="sm" aria-label="List view">
                  <List className="h-4 w-4" />
                </ToggleGroupItem>
              </ToggleGroup>

              <Separator
                orientation="vertical"
                className="hidden h-6 sm:block"
              />

              {/* Filter trigger (mobile) */}
              <div className="lg:hidden">
                <FilterTrigger
                  activeCount={activeFilterCount}
                  onClick={() => setShowFilters(true)}
                />
              </div>

              {/* Save search */}
              <Button variant="outline" size="sm" onClick={handleSaveSearch}>
                <Bookmark className="mr-2 h-4 w-4" />
                Save
              </Button>

              {/* Chat refinement */}
              <Button variant="outline" size="sm" asChild>
                <Link href="/chat">
                  <MessageSquare className="mr-2 h-4 w-4" />
                  Refine with AI
                </Link>
              </Button>
            </div>
          </div>

          {/* Content with filters */}
          <div className="flex gap-6">
            {/* Filter sidebar (desktop) */}
            <aside className="hidden w-64 flex-shrink-0 lg:block">
              <FilterPanel
                facets={mockFilterFacets}
                filters={filters}
                onFiltersChange={setFilters}
              />
            </aside>

            {/* Filter sheet (mobile) */}
            <Sheet open={showFilters} onOpenChange={setShowFilters}>
              <SheetContent side="left" className="w-80">
                <SheetHeader>
                  <SheetTitle>Filters</SheetTitle>
                </SheetHeader>
                <div className="mt-4">
                  <FilterPanel
                    facets={mockFilterFacets}
                    filters={filters}
                    onFiltersChange={setFilters}
                    className="border-0 shadow-none"
                  />
                </div>
              </SheetContent>
            </Sheet>

            {/* Results */}
            <div className="min-w-0 flex-1">
              {isLoading ? (
                <ResultsLoadingSkeleton viewMode={viewMode} />
              ) : displayResults.length === 0 ? (
                <EmptyResults query={query} />
              ) : (
                <>
                  {/* Results grid/list */}
                  <div
                    className={cn(
                      viewMode === "grid"
                        ? "grid grid-cols-1 gap-4 sm:grid-cols-2 xl:grid-cols-3"
                        : "space-y-3"
                    )}
                  >
                    {displayResults.map((result) => (
                      <div key={result.asset.id} className="space-y-2">
                        <AssetCard
                          asset={result.asset}
                          showMatchInfo
                          matchExplanation={result.matchReasons[0]?.explanation}
                        />
                        {viewMode === "list" && (
                          <WhyMatched
                            matchReasons={result.matchReasons}
                            relevanceScore={result.relevanceScore}
                            variant="expanded"
                          />
                        )}
                      </div>
                    ))}
                  </div>

                  {/* Load more */}
                  <div className="mt-8 text-center">
                    <Button variant="outline">
                      Load more results
                      <ChevronDown className="ml-2 h-4 w-4" />
                    </Button>
                  </div>

                  {/* Explore more section */}
                  <div className="mt-12">
                    <ExploreMore suggestions={mockExploreSuggestions} />
                  </div>
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </AppShell>
  );
}

function ResultsLoadingSkeleton({ viewMode }: { viewMode: "grid" | "list" }) {
  return (
    <div
      className={cn(
        viewMode === "grid"
          ? "grid grid-cols-1 gap-4 sm:grid-cols-2 xl:grid-cols-3"
          : "space-y-3"
      )}
    >
      {Array.from({ length: 6 }).map((_, i) => (
        <Card key={i} className="overflow-hidden">
          <Skeleton className="aspect-[4/3]" />
          <div className="space-y-2 p-3">
            <Skeleton className="h-4 w-3/4" />
            <Skeleton className="h-3 w-1/2" />
            <div className="flex gap-1">
              <Skeleton className="h-5 w-12" />
              <Skeleton className="h-5 w-16" />
            </div>
          </div>
        </Card>
      ))}
    </div>
  );
}

function EmptyResults({ query }: { query: string }) {
  return (
    <div className="flex flex-col items-center justify-center py-16 text-center">
      <div className="bg-muted mb-4 flex h-16 w-16 items-center justify-center rounded-full">
        <Sparkles className="text-muted-foreground h-8 w-8" />
      </div>
      <h2 className="mb-2 text-xl font-semibold">No results found</h2>
      <p className="text-muted-foreground mb-6 max-w-md">
        We couldn&apos;t find any assets matching &ldquo;{query}&rdquo;. Try
        adjusting your search or using different keywords.
      </p>
      <div className="flex gap-3">
        <Button variant="outline" asChild>
          <Link href="/">Clear search</Link>
        </Button>
        <Button asChild>
          <Link href="/chat">
            <MessageSquare className="mr-2 h-4 w-4" />
            Ask AI for help
          </Link>
        </Button>
      </div>
    </div>
  );
}

export default function SearchResultsPage() {
  return (
    <Suspense fallback={<ResultsLoadingSkeleton viewMode="grid" />}>
      <SearchResultsContent />
    </Suspense>
  );
}

"use client";

import { useState } from "react";
import {
  Search,
  SlidersHorizontal,
  X,
  FileType,
  Tag as TagIcon,
} from "lucide-react";
import { Button } from "@repo/ui";
import { Badge } from "@repo/ui";
import { cn } from "@repo/ui";

interface AdvancedSearchBarProps {
  onSearch?: (query: string, filters: SearchFilters) => void;
}

interface SearchFilters {
  fileType?: string;
  tags?: string[];
}

export function AdvancedSearchBar({ onSearch }: AdvancedSearchBarProps) {
  const [query, setQuery] = useState("");
  const [showFilters, setShowFilters] = useState(false);
  const [fileType, setFileType] = useState<string>("");
  const [selectedTags, setSelectedTags] = useState<string[]>([]);

  const fileTypes = ["image", "video", "document", "audio"];
  const commonTags = ["marketing", "product", "design", "social"];

  const handleSearch = (e?: React.FormEvent) => {
    e?.preventDefault();
    if (onSearch && query.trim()) {
      onSearch(query, { fileType, tags: selectedTags });
    }
  };

  const toggleTag = (tag: string) => {
    setSelectedTags((prev) =>
      prev.includes(tag) ? prev.filter((t) => t !== tag) : [...prev, tag]
    );
  };

  const clearAll = () => {
    setQuery("");
    setFileType("");
    setSelectedTags([]);
  };

  const filterCount = (fileType ? 1 : 0) + selectedTags.length;

  return (
    <div className="relative w-full">
      <form onSubmit={handleSearch} className="relative">
        <div className="relative flex items-center">
          <div className="text-muted-foreground pointer-events-none absolute left-3">
            <Search className="h-4 w-4" />
          </div>

          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Search assets by name, tags, type..."
            className="bg-background placeholder:text-muted-foreground focus:border-primary focus:ring-primary h-9 w-full rounded-md border pl-9 pr-28 text-sm transition-colors focus:outline-none focus:ring-1"
          />

          <div className="absolute right-2 flex items-center gap-1">
            {(query || filterCount > 0) && (
              <Button
                type="button"
                variant="ghost"
                size="icon"
                onClick={clearAll}
                className="h-6 w-6"
              >
                <X className="h-3 w-3" />
              </Button>
            )}

            <Button
              type="button"
              variant={showFilters ? "secondary" : "ghost"}
              size="sm"
              onClick={() => setShowFilters(!showFilters)}
              className="h-7 gap-1 px-2"
            >
              <SlidersHorizontal className="h-3.5 w-3.5" />
              {filterCount > 0 && (
                <span className="bg-primary text-primary-foreground flex h-4 min-w-4 items-center justify-center rounded-full text-[10px] font-medium">
                  {filterCount}
                </span>
              )}
            </Button>
          </div>
        </div>
      </form>

      {/* Compact Filter Dropdown */}
      {showFilters && (
        <div className="animate-in fade-in slide-in-from-top-2 bg-background absolute left-0 right-0 top-full z-50 mt-2 rounded-lg border p-3 shadow-lg">
          <div className="space-y-3">
            {/* File Type */}
            <div className="space-y-2">
              <div className="text-muted-foreground flex items-center gap-1.5 text-xs font-medium">
                <FileType className="h-3 w-3" />
                Type
              </div>
              <div className="flex flex-wrap gap-1.5">
                {fileTypes.map((type) => (
                  <Badge
                    key={type}
                    variant={fileType === type ? "default" : "outline"}
                    className="cursor-pointer text-xs capitalize"
                    onClick={() => setFileType(fileType === type ? "" : type)}
                  >
                    {type}
                  </Badge>
                ))}
              </div>
            </div>

            {/* Tags */}
            <div className="space-y-2">
              <div className="text-muted-foreground flex items-center gap-1.5 text-xs font-medium">
                <TagIcon className="h-3 w-3" />
                Tags
              </div>
              <div className="flex flex-wrap gap-1.5">
                {commonTags.map((tag) => (
                  <Badge
                    key={tag}
                    variant={selectedTags.includes(tag) ? "default" : "outline"}
                    className="cursor-pointer text-xs capitalize"
                    onClick={() => toggleTag(tag)}
                  >
                    {tag}
                  </Badge>
                ))}
              </div>
            </div>

            {/* Actions */}
            <div className="flex justify-end gap-2 border-t pt-2">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowFilters(false)}
                className="h-7 text-xs"
              >
                Close
              </Button>
              <Button
                size="sm"
                onClick={() => {
                  handleSearch();
                  setShowFilters(false);
                }}
                disabled={!query.trim()}
                className="h-7 text-xs"
              >
                Search
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

"use client";

import { useState } from "react";
import Image from "next/image";
import { Badge, Card, Checkbox } from "@repo/ui";
import { cn } from "@repo/ui";
import { Play, FileText, Headphones, Image as ImageIcon } from "lucide-react";
import type { Asset } from "@/lib/types";
import { formatDate, formatDuration, formatFileSize } from "@/lib/mock-data";
import { useSelectionStore } from "@/lib/stores/selection-store";
import { QuickLookDialog } from "@/components/quick-look-dialog";
import { AssetContextMenu } from "@/components/asset-context-menu";

interface AssetCardProps {
  asset: Asset;
  onClick?: () => void;
  showMatchInfo?: boolean;
  matchExplanation?: string;
  className?: string;
}

const typeIcons = {
  image: ImageIcon,
  video: Play,
  document: FileText,
  audio: Headphones,
} as const;

const typeColors = {
  image: "bg-emerald-500",
  video: "bg-rose-500",
  document: "bg-blue-500",
  audio: "bg-violet-500",
} as const;

export function AssetCard({
  asset,
  onClick,
  showMatchInfo = false,
  matchExplanation,
  className,
}: AssetCardProps) {
  const TypeIcon = typeIcons[asset.type];
  const { isSelected, toggleAsset, selectAsset } = useSelectionStore();
  const selected = isSelected(asset.id);
  const [quickLookOpen, setQuickLookOpen] = useState(false);

  const handleCardClick = (e: React.MouseEvent) => {
    // Check if Cmd/Ctrl is pressed for multi-select
    if (e.metaKey || e.ctrlKey) {
      e.preventDefault();
      toggleAsset(asset);
      return;
    }

    // Regular click
    if (onClick) {
      onClick();
    }
  };

  const handleDoubleClick = () => {
    setQuickLookOpen(true);
  };

  const handleCheckboxChange = (e: React.MouseEvent) => {
    e.stopPropagation();
    toggleAsset(asset);
  };

  return (
    <>
      <AssetContextMenu asset={asset} onPreview={() => setQuickLookOpen(true)}>
        <Card
          className={cn(
            "group relative cursor-pointer overflow-hidden transition-all duration-200",
            "hover:ring-primary/20 hover:-translate-y-0.5 hover:shadow-lg hover:ring-2",
            selected && "ring-primary ring-2",
            className
          )}
          onClick={handleCardClick}
          onDoubleClick={handleDoubleClick}
        >
          {/* Selection Checkbox */}
          <div
            className={cn(
              "absolute left-2 top-2 z-10 transition-opacity",
              selected ? "opacity-100" : "opacity-0 group-hover:opacity-100"
            )}
            onClick={handleCheckboxChange}
          >
            <div className="bg-background/90 rounded-md p-0.5 shadow-sm backdrop-blur">
              <Checkbox checked={selected} />
            </div>
          </div>

          {/* Thumbnail */}
          <div className="bg-muted relative aspect-[4/3] overflow-hidden">
            <Image
              src={asset.thumbnail}
              alt={asset.title}
              fill
              unoptimized
              className="object-cover transition-transform duration-300 group-hover:scale-105"
              sizes="(max-width: 640px) 100vw, (max-width: 1024px) 50vw, 25vw"
            />

            {/* Type badge overlay */}
            <div className="absolute right-2 top-2">
              <div
                className={cn(
                  "flex h-7 w-7 items-center justify-center rounded-full text-white shadow-md",
                  typeColors[asset.type]
                )}
              >
                <TypeIcon className="h-3.5 w-3.5" />
              </div>
            </div>

            {/* Duration badge for video/audio */}
            {asset.duration && (
              <div className="absolute bottom-2 right-2 rounded bg-black/70 px-2 py-0.5 text-xs text-white">
                {formatDuration(asset.duration)}
              </div>
            )}
          </div>

          {/* Content */}
          <div className="p-3">
            {/* Title */}
            <h3 className="group-hover:text-primary truncate text-sm font-medium transition-colors">
              {asset.title}
            </h3>

            {/* Meta info */}
            <div className="text-muted-foreground mt-1.5 flex items-center gap-2 text-xs">
              <span>{formatDate(asset.createdAt)}</span>
              {asset.size && (
                <>
                  <span>â€¢</span>
                  <span>{formatFileSize(asset.size)}</span>
                </>
              )}
            </div>

            {/* Tags */}
            <div className="mt-2 flex flex-wrap gap-1">
              {asset.tags.slice(0, 3).map((tag) => (
                <Badge
                  key={tag}
                  variant="outline"
                  className="px-1.5 py-0 text-xs"
                >
                  {tag}
                </Badge>
              ))}
              {asset.tags.length > 3 && (
                <Badge variant="outline" className="px-1.5 py-0 text-xs">
                  +{asset.tags.length - 3}
                </Badge>
              )}
            </div>

            {/* Match explanation */}
            {showMatchInfo && matchExplanation && (
              <div className="mt-2 border-t pt-2">
                <p className="text-muted-foreground line-clamp-2 text-xs">
                  <span className="text-primary font-medium">Matched:</span>{" "}
                  {matchExplanation}
                </p>
              </div>
            )}
          </div>
        </Card>
      </AssetContextMenu>

      <QuickLookDialog
        asset={asset}
        open={quickLookOpen}
        onOpenChange={setQuickLookOpen}
      />
    </>
  );
}

"use client";

import { useState, useCallback } from "react";
import { useRouter } from "next/navigation";
import {
  CommandDialog,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
  CommandSeparator,
} from "@repo/ui";
import {
  Search,
  Clock,
  Bookmark,
  FileText,
  Image,
  FileIcon,
  Sparkles,
} from "lucide-react";
import { useSearchStore } from "@/lib/stores/search-store";
import type { SearchMode } from "@/lib/types";

interface CommandPaletteProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function CommandPalette({ open, onOpenChange }: CommandPaletteProps) {
  const router = useRouter();
  const {
    query,
    setQuery,
    mode,
    setMode,
    recentSearches,
    savedSearches,
    search,
  } = useSearchStore();
  const [localQuery, setLocalQuery] = useState("");

  const handleSearch = useCallback(
    async (searchQuery: string, searchMode: SearchMode = mode) => {
      setQuery(searchQuery);
      setMode(searchMode);

      // Close palette
      onOpenChange(false);

      // Execute search and navigate
      await search();
      router.push(
        `/search?q=${encodeURIComponent(searchQuery)}&mode=${searchMode}`
      );
    },
    [mode, setQuery, setMode, onOpenChange, search, router]
  );

  const handleRecentSearch = (term: string) => {
    handleSearch(term, "global");
  };

  const handleSavedSearch = (savedQuery: string) => {
    handleSearch(savedQuery, "global");
  };

  const handleModeSwitch = (newMode: SearchMode) => {
    setMode(newMode);
    if (localQuery) {
      handleSearch(localQuery, newMode);
    }
  };

  return (
    <CommandDialog open={open} onOpenChange={onOpenChange}>
      <CommandInput
        placeholder="Search assets, folders, or type a command..."
        value={localQuery}
        onValueChange={setLocalQuery}
      />
      <CommandList>
        <CommandEmpty>No results found.</CommandEmpty>

        {/* Search Modes */}
        <CommandGroup heading="Search Modes">
          <CommandItem onSelect={() => handleModeSwitch("global")}>
            <Search className="mr-2 h-4 w-4" />
            <span>Global Search</span>
            {mode === "global" && (
              <span className="text-muted-foreground ml-auto text-xs">
                Active
              </span>
            )}
          </CommandItem>
          <CommandItem onSelect={() => handleModeSwitch("semantic")}>
            <Sparkles className="mr-2 h-4 w-4" />
            <span>AI Semantic Search</span>
            {mode === "semantic" && (
              <span className="text-muted-foreground ml-auto text-xs">
                Active
              </span>
            )}
          </CommandItem>
          <CommandItem onSelect={() => handleModeSwitch("advanced")}>
            <FileText className="mr-2 h-4 w-4" />
            <span>Advanced Search</span>
            {mode === "advanced" && (
              <span className="text-muted-foreground ml-auto text-xs">
                Active
              </span>
            )}
          </CommandItem>
        </CommandGroup>

        <CommandSeparator />

        {/* Recent Searches */}
        {recentSearches && recentSearches.length > 0 && (
          <>
            <CommandGroup heading="Recent">
              {recentSearches.slice(0, 5).map((recent) => (
                <CommandItem
                  key={recent.id}
                  onSelect={() => handleRecentSearch(recent.query)}
                >
                  <Clock className="text-muted-foreground mr-2 h-4 w-4" />
                  <span>{recent.query}</span>
                  <span className="text-muted-foreground ml-auto text-xs">
                    {recent.resultCount} results
                  </span>
                </CommandItem>
              ))}
            </CommandGroup>
            <CommandSeparator />
          </>
        )}

        {/* Saved Searches */}
        {savedSearches && savedSearches.length > 0 && (
          <>
            <CommandGroup heading="Saved Searches">
              {savedSearches.map((saved) => (
                <CommandItem
                  key={saved.id}
                  onSelect={() => handleSavedSearch(saved.query)}
                >
                  <Bookmark className="text-muted-foreground mr-2 h-4 w-4" />
                  <span>{saved.name}</span>
                </CommandItem>
              ))}
            </CommandGroup>
            <CommandSeparator />
          </>
        )}

        {/* Quick Actions */}
        <CommandGroup heading="Quick Actions">
          <CommandItem
            onSelect={() => {
              onOpenChange(false);
              router.push("/assets");
            }}
          >
            <FileIcon className="mr-2 h-4 w-4" />
            <span>Browse All Assets</span>
          </CommandItem>
          <CommandItem
            onSelect={() => {
              onOpenChange(false);
              router.push("/collections");
            }}
          >
            <Image className="mr-2 h-4 w-4" />
            <span>View Collections</span>
          </CommandItem>
          <CommandItem
            onSelect={() => {
              onOpenChange(false);
              router.push("/chat");
            }}
          >
            <Sparkles className="mr-2 h-4 w-4" />
            <span>AI Chat Search</span>
          </CommandItem>
        </CommandGroup>

        {/* Search on Enter */}
        {localQuery && (
          <>
            <CommandSeparator />
            <CommandGroup>
              <CommandItem
                onSelect={() => handleSearch(localQuery, mode)}
                className="bg-primary/5"
              >
                <Search className="mr-2 h-4 w-4" />
                <span>Search for "{localQuery}"</span>
                <kbd className="bg-muted ml-auto rounded px-1.5 py-0.5 text-xs">
                  Enter
                </kbd>
              </CommandItem>
            </CommandGroup>
          </>
        )}
      </CommandList>
    </CommandDialog>
  );
}

